import { ChatService } from './../../services/chat/chat.service';
import { Component, OnInit, ViewChild } from '@angular/core';
import { NavigationExtras, Router } from '@angular/router';
import { ModalController, PopoverController } from '@ionic/angular';
import { Observable, take } from 'rxjs';

@Component({
  selector: 'app-home',
  templateUrl: './home.page.html',
  styleUrls: ['./home.page.scss'],
})
export class HomePage implements OnInit {

  @ViewChild('new_chat') modal: ModalController;
  @ViewChild('popover') popover: PopoverController;
  segment = 'chats';
  open_new_chat = false;
  users: Observable<any[]>;
  chatRooms: Observable<any[]>;
  model = {
    icon: 'chatbubbles-outline',
    title: 'No Chat Rooms',
    color: 'danger'
  };
  // users = [
  //   {id: 1, name: 'NIkhil', photo: 'https://i.pravatar.cc/315'},
  //   {id: 2, name: 'XYZ', photo: 'https://i.pravatar.cc/325'},
  // ];
  // chatRooms = [
  //   {id: 1, name: 'NIkhil', photo: 'https://i.pravatar.cc/315'},
  //   {id: 2, name: 'XYZ', photo: 'https://i.pravatar.cc/325'},
  // ];

  constructor(
    private router: Router,
    private chatService: ChatService
  ) { }

  ngOnInit() {
    this.getRooms();
  }

  getRooms() {
    // this.chatService.getId();
    this.chatService.getChatRooms();
    this.chatRooms = this.chatService.chatRooms;
    console.log('chatrooms: ', this.chatRooms);
  }

  async logout() {
    try {
      console.log('logout');
      this.popover.dismiss();
      await this.chatService.auth.logout();
      // this.chatService.currentUserId = null;
      this.router.navigateByUrl('/login', {replaceUrl: true});
    } catch(e) {
      console.log(e);
    }
  }

  onSegmentChanged(event: any) {
    console.log(event);
    this.segment = event.detail.value;
  }

  newChat() {
    this.open_new_chat = true;
    if(!this.users) this.getUsers();
  }

  getUsers() {
    this.chatService.getUsers();
    this.users = this.chatService.users;
  }

  onWillDismiss(event: any) {}

  cancel() {
    this.modal.dismiss();
    this.open_new_chat = false;
  }

  async startChat(item) {
    try {
      // this.global.showLoader();
      // create chatroom
      const room = await this.chatService.createChatRoom(item?.uid);
      console.log('room: ', room);
      this.cancel();
      const navData: NavigationExtras = {
        queryParams: {
          name: item?.name
        }
      };
      this.router.navigate(['/', 'home', 'chats', room?.id], navData);
      // this.global.hideLoader();
    } catch(e) {
      console.log(e);
      // this.global.hideLoader();
    }
  }

  getChat(item) {
    (item?.user).pipe(
      take(1)
    ).subscribe(user_data => {
      console.log('data: ', user_data);
      const navData: NavigationExtras = {
        queryParams: {
          name: user_data?.name
        }
      };
      this.router.navigate(['/', 'home', 'chats', item?.id], navData);
    });
  }

  getUser(user: any) {
    return user;
  }

}
<ion-header class="title-header">
  <ion-toolbar color="primary">
    <ion-title>Chat App</ion-title>
    <ion-buttons slot="end">
      <ion-button fill="clear" id="popover">
        <ion-icon slot="icon-only" name="ellipsis-vertical-outline"></ion-icon>
      </ion-button>
    </ion-buttons>
    <ion-popover #popover trigger="popover" triggerAction="click">
      <ng-template>
        <ion-content class="ion-padding ion-text-center" (click)="logout()">
          <ion-label color="danger">
            Cerrar Sesion
          </ion-label>
        </ion-content>
      </ng-template>
    </ion-popover>
  </ion-toolbar>
  <ion-segment [value]="segment" mode="md" (ionChange)="onSegmentChanged($event)">
    <ion-segment-button value="status">
      <ion-label>Estado</ion-label>
    </ion-segment-button>
    <ion-segment-button value="chats">
      <ion-label>Chats</ion-label>
    </ion-segment-button>
    <ion-segment-button value="calls">
      <ion-label>Llamadas</ion-label>
    </ion-segment-button>
  </ion-segment>
</ion-header>

<ion-content color="light">

  <app-empty-screen 
    *ngIf="(chatRooms | async)?.length == 0" 
    [model]="model">
  </app-empty-screen>

  <ng-container *ngIf="segment == 'chats' && (chatRooms | async) as rooms">

    <ion-list *ngIf="rooms?.length > 0">

      <ion-list-header class="ion-padding-bottom" color="light">
        RECENT CHATS
      </ion-list-header>

      <ion-item
        *ngFor="let item of rooms" 
        (click)="getChat(item)"
        [detail]="true">
        <ng-container *ngIf="(item?.user | async) as user">
          <ion-avatar slot="start">
            <img [src]="getUser(user)?.photo" />
          </ion-avatar>
          <ion-label>
            {{getUser(user)?.name}}
            <!-- <p>
              <ion-text>
                message
              </ion-text>
            </p> -->
          </ion-label>
          <!-- <ion-text slot="end" color="medium">10:00 AM</ion-text> -->
        </ng-container>
      </ion-item>

    </ion-list>

  </ng-container>


  <ion-fab slot="fixed" horizontal="end" vertical="bottom">
    <ion-fab-button (click)="newChat()">
      <ion-icon name="add"></ion-icon>
    </ion-fab-button>
  </ion-fab>

  <ion-modal #new_chat [isOpen]="open_new_chat" (willDismiss)="onWillDismiss($event)">
    <ng-template>
      <ion-header>
        <ion-toolbar color="primary">
          <ion-title>New Chat</ion-title>
          <ion-buttons slot="end">
            <ion-button (click)="cancel()">Cancel</ion-button>
          </ion-buttons>
        </ion-toolbar>
      </ion-header>
      <ion-content color="light" *ngIf="(users | async) as user$">
        <ion-list class="ion-margin-top" [style.background]="'transparent'" lines="none">
          <app-user-list 
          *ngFor="let item of user$" 
          [item]="item"
          (onClick)="startChat($event)">
        </app-user-list>
        </ion-list>
      </ion-content>
    </ng-template>
  </ion-modal>

</ion-content>
